#include "textureUtils.h"\n#include <libdragon.h>\n#include <stdio.h>\n\nint main() {\n    // Initialize libdragon\n    console_init();\n    console_set_render_mode(RENDER_AUTOMATIC);\n    \n    // Test loading a texture (this will use the test pattern since the file doesn't exist)\n    surface_t testTexture = load_rgb555_texture("test.rgb555", 64, 64);\n    \n    // Print information about the texture\n    printf("Texture loaded:\\n");\n    printf("Width: %d\\n", testTexture.width);\n    printf("Height: %d\\n", testTexture.height);\n    printf("Format: %d\\n", testTexture.format);\n    \n    // Display a message\n    printf("Texture loading test complete.\\n");\n    printf("Press any key to exit.\\n");\n    \n    // Wait for input\n    while (!get_keys_down()) {\n        console_render();\n    }\n    \n    // Clean up\n    surface_free(&testTexture);\n    \n    return 0;\n}